import React, { useEffect, useState } from "react";
import { useSelector } from "react-redux";
import { selectUser } from "../features/userSlice";
import Nav from "../components/Nav";
import "./ProfileScreen.css";
import db, { auth } from "../firebase";
import PlansComponent from "../components/PlansComponent";
import { collection, getDocs, query, where } from "firebase/firestore";

function ProfileScreen() {
  const user = useSelector(selectUser);
  const [products, setProducts] = useState([]);

  useEffect(() => {
    const q = query(collection(db, "products"), where("active", "==", true));
    async function snapShot() {
      const querySnapshot = await getDocs(q);
      querySnapshot.forEach((doc) => {
        // doc.data() is never undefined for query doc snapshots
        console.log(doc.id, " => ", doc.data());
      });
    }
    snapShot();
    // db.collection("products")
    //   .where("active", "==", true)
    //   .get()
    //   .then((QuerySnapshot) => {
    //     const products = [];
    //     QuerySnapshot.forEach(async (productDoc) => {
    //       products[productDoc.id] = productDoc.data();
    //       const priceSnap = await productDoc.ref.collection("prices").get();
    //       priceSnap.docs.forEach((price) => {
    //         products[productDoc.id].prices = {
    //           priceId: price.id,
    //           priceData: price.data(),
    //         };
    //       });
    //     });
    //     setProducts(products);
    //   });
  }, []);

  console.log(products);
  return (
    <div className="profileScreen">
      <Nav />
      <div className="profileScreen__body">
        <h1>Edit Profile</h1>
        <div className="profileScreen__info">
          <img
            className="profileScreen__icon"
            src="https://ih0.redbubble.net/image.618427277.3222/flat,1000x1000,075,f.u2.jpg"
            alt="user-icon"
          />
          <div className="profileScreen__details">
            <h2>{user.email}</h2>
            <div className="profileScreen__plans">
              <h3>Plans (Current Plan: Premium)</h3>
              <p>Renewal date: 04/03/2023</p>
              <PlansComponent type="Standard" res="1080p" />
              <PlansComponent type="Basic" res="480p" />
              <PlansComponent type="Premium" res="4k+HDR" isCurrent />
              <button
                className="profileScreen__signOut"
                onClick={() => auth.signOut()}
              >
                Sign Out
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default ProfileScreen;
